import React, { useState, useEffect } from 'react';
import Whiteboard from './Whiteboard';
import NotesEditor from './NotesEditor';
import UsersList from './UsersList';
import RoomHeader from './RoomHeader';
import VoiceChat from './VoiceChat';
import ScreenShare from './ScreenShare';
import AITextRecognition from './AITextRecognition';
import RealTimePolls from './RealTimePolls';
import PomodoroTimer from './PomodoroTimer';
import FileUpload from './FileUpload';
import ActivityFeed from './ActivityFeed';
import QuickTemplates from './QuickTemplates';
import PresentationMode from './PresentationMode';
import RoomAnalytics from './RoomAnalytics';
import CodeEditor from './CodeEditor';
import EmojiReactions from './EmojiReactions';
import LaserPointer from './LaserPointer';
import RoomThemes from './RoomThemes';
import ExportShare from './ExportShare';
import MindMapGenerator from './MindMapGenerator';
import FeatureShowcase from './FeatureShowcase';
import RealTimeChat from './RealTimeChat';
import LiveNotifications from './LiveNotifications';

const CollaborationRoom = ({ user, roomId, socket, onLeaveRoom, showToast }) => {
  const [activeTab, setActiveTab] = useState('whiteboard');
  const [users, setUsers] = useState([]);
  const [roomData, setRoomData] = useState({
    whiteboardData: [],
    notesData: ''
  });

  // Handle socket events
  useEffect(() => {
    if (!socket) return;

    // Handle room data loading
    const handleRoomData = (data) => {
      setRoomData({
        whiteboardData: data.whiteboardData || [],
        notesData: data.notesData || ''
      });
    };

    // Handle users list updates
    const handleUsersUpdate = (usersList) => {
      setUsers(usersList);
    };

    // Handle user join notifications
    const handleUserJoined = (data) => {
      if (data.username !== user.username) {
        showToast(`${data.username} joined the room`, 'info');
      }
    };

    // Handle user leave notifications
    const handleUserLeft = (data) => {
      showToast(`${data.username} left the room`, 'info');
    };

    // Handle connection errors
    const handleError = (error) => {
      showToast(error.message || 'An error occurred', 'error');
    };

    // Register event listeners
    socket.on('room-data', handleRoomData);
    socket.on('users-update', handleUsersUpdate);
    socket.on('user-joined', handleUserJoined);
    socket.on('user-left', handleUserLeft);
    socket.on('error', handleError);

    return () => {
      socket.off('room-data', handleRoomData);
      socket.off('users-update', handleUsersUpdate);
      socket.off('user-joined', handleUserJoined);
      socket.off('user-left', handleUserLeft);
      socket.off('error', handleError);
    };
  }, [socket, user.username, showToast]);

  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-50 via-white to-purple-50">
      {/* Room Header */}
      <RoomHeader
        user={user}
        roomId={roomId}
        onLeaveRoom={onLeaveRoom}
        showToast={showToast}
      />

      <div className="flex flex-1 h-[calc(100vh-4rem)]">
        {/* Main Content Area */}
        <div className="flex-1 flex flex-col">
          {/* Tab Navigation */}
          <div className="bg-white/80 backdrop-blur-sm border-b border-gray-200/50 shadow-sm">
            <div className="px-6">
              <nav className="flex space-x-4 overflow-x-auto">
                <button
                  onClick={() => handleTabChange('whiteboard')}
                  className={`py-3 px-4 border-b-3 font-semibold text-sm transition-all duration-200 whitespace-nowrap ${activeTab === 'whiteboard'
                    ? 'border-blue-500 text-blue-600 bg-blue-50/50'
                    : 'border-transparent text-gray-600 hover:text-blue-600 hover:border-blue-300 hover:bg-blue-50/30'
                    }`}
                >
                  🎨 Whiteboard
                </button>
                <button
                  onClick={() => handleTabChange('notes')}
                  className={`py-3 px-4 border-b-3 font-semibold text-sm transition-all duration-200 whitespace-nowrap ${activeTab === 'notes'
                    ? 'border-green-500 text-green-600 bg-green-50/50'
                    : 'border-transparent text-gray-600 hover:text-green-600 hover:border-green-300 hover:bg-green-50/30'
                    }`}
                >
                  📝 Notes
                </button>
                <button
                  onClick={() => handleTabChange('code')}
                  className={`py-3 px-4 border-b-3 font-semibold text-sm transition-all duration-200 whitespace-nowrap ${activeTab === 'code'
                    ? 'border-purple-500 text-purple-600 bg-purple-50/50'
                    : 'border-transparent text-gray-600 hover:text-purple-600 hover:border-purple-300 hover:bg-purple-50/30'
                    }`}
                >
                  � Code Editor
                </button>
                <button
                  onClick={() => handleTabChange('mindmap')}
                  className={`py-3 px-4 border-b-3 font-semibold text-sm transition-all duration-200 whitespace-nowrap ${activeTab === 'mindmap'
                    ? 'border-pink-500 text-pink-600 bg-pink-50/50'
                    : 'border-transparent text-gray-600 hover:text-pink-600 hover:border-pink-300 hover:bg-pink-50/30'
                    }`}
                >
                  🧠 Mind Maps
                </button>
                <button
                  onClick={() => handleTabChange('polls')}
                  className={`py-3 px-4 border-b-3 font-semibold text-sm transition-all duration-200 whitespace-nowrap ${activeTab === 'polls'
                    ? 'border-teal-500 text-teal-600 bg-teal-50/50'
                    : 'border-transparent text-gray-600 hover:text-teal-600 hover:border-teal-300 hover:bg-teal-50/30'
                    }`}
                >
                  📊 Live Polls
                </button>
                <button
                  onClick={() => handleTabChange('templates')}
                  className={`py-3 px-4 border-b-3 font-semibold text-sm transition-all duration-200 whitespace-nowrap ${activeTab === 'templates'
                    ? 'border-indigo-500 text-indigo-600 bg-indigo-50/50'
