          </div>
        </div>

        {/* Sidebar - Users List & Features */}
        <div className="w-80 bg-gradient-to-b from-white to-gray-50 border-l border-gray-200 flex flex-col shadow-lg">
          <div className="flex-1 overflow-y-auto custom-scrollbar">
            <FeatureShowcase showToast={showToast} />
            <RealTimeChat
              socket={socket}
              roomId={roomId}
              currentUser={user}
              showToast={showToast}
            />
            <UsersList users={users} currentUser={user.username} />
            <AITextRecognition
              socket={socket}
              roomId={roomId}
              showToast={showToast}
            />
            <RealTimePolls
              socket={socket}
              roomId={roomId}
              currentUser={user}
              showToast={showToast}
            />
            <PomodoroTimer
              socket={socket}
              roomId={roomId}
              showToast={showToast}
            />
            <FileUpload
              socket={socket}
              roomId={roomId}
              showToast={showToast}
            />
            <QuickTemplates
              socket={socket}
              roomId={roomId}
              showToast={showToast}
            />
            <MindMapGenerator
              socket={socket}
              roomId={roomId}
              showToast={showToast}
            />
            <PresentationMode
              socket={socket}
              roomId={roomId}
              showToast={showToast}
            />
            <CodeEditor
              socket={socket}
              roomId={roomId}
              showToast={showToast}
            />
            <EmojiReactions
              socket={socket}
              roomId={roomId}
            />
            <LaserPointer
              socket={socket}
              roomId={roomId}
              currentUser={user}
              showToast={showToast}
            />
            <RoomThemes
              socket={socket}
              roomId={roomId}
              showToast={showToast}
            />
            <ExportShare
              socket={socket}
              roomId={roomId}
              showToast={showToast}
            />
            <RoomAnalytics
              socket={socket}
              roomId={roomId}
              users={users}
            />
            <ActivityFeed
              socket={socket}
              roomId={roomId}
            />
          </div>
          <ScreenShare
            socket={socket}
            roomId={roomId}
            currentUser={user}
            showToast={showToast}
          />
          <VoiceChat
            socket={socket}
            roomId={roomId}
            currentUser={user}
            showToast={showToast}
          />
        </div>
      </div>

      {/* Live Notifications */}
      <LiveNotifications socket={socket} roomId={roomId} />

      {/* Mobile Tab Switcher (visible on small screens) */}
      <div className="md:hidden fixed bottom-0 left-0 right-0 bg-white/90 backdrop-blur-sm border-t border-gray-200/50 px-4 py-2 shadow-lg">
        <div className="flex space-x-4">
          <button
            onClick={() => handleTabChange('whiteboard')}
            className={`flex-1 py-3 px-4 rounded-xl text-sm font-semibold transition-all duration-200 ${activeTab === 'whiteboard'
              ? 'bg-gradient-to-r from-blue-500 to-purple-500 text-white shadow-lg transform scale-105'
              : 'bg-gray-100 text-gray-700 hover:bg-gray-200 hover:scale-102'
              }`}
          >
            🎨 Whiteboard
          </button>
          <button
            onClick={() => handleTabChange('notes')}
            className={`flex-1 py-3 px-4 rounded-xl text-sm font-semibold transition-all duration-200 ${activeTab === 'notes'
              ? 'bg-gradient-to-r from-green-500 to-blue-500 text-white shadow-lg transform scale-105'
              : 'bg-gray-100 text-gray-700 hover:bg-gray-200 hover:scale-102'
              }`}
          >
            📝 Notes
          </button>
