import React, { useState } from 'react';

const FeatureShowcase = ({ showToast }) => {
  const [showShowcase, setShowShowcase] = useState(false);

  const features = [
    {
      category: 'üé® Creative Tools',
      items: [
        { name: 'Advanced Drawing Tools', description: 'Pen, eraser, shapes, colors', status: '‚úÖ' },
        { name: 'Sticky Notes', description: 'Collaborative note-taking', status: '‚úÖ' },
        { name: 'Quick Templates', description: 'Pre-made layouts & frameworks', status: '‚úÖ' },
        { name: 'AI Mind Maps', description: 'Auto-generated mind maps', status: '‚úÖ' },
        { name: 'Room Themes', description: 'Dynamic visual themes', status: '‚úÖ' }
      ]
    },
    {
      category: 'ü§ù Collaboration',
      items: [
        { name: 'Real-time Cursors', description: 'See where others are pointing', status: '‚úÖ' },
        { name: 'Voice Chat', description: 'Audio messages & communication', status: '‚úÖ' },
        { name: 'Screen Sharing', description: 'Share your screen live', status: '‚úÖ' },
        { name: 'Live Polls', description: 'Interactive voting system', status: '‚úÖ' },
        { name: 'Emoji Reactions', description: 'Express emotions instantly', status: '‚úÖ' }
      ]
    },
    {
      category: 'üíª Development',
      items: [
        { name: 'Code Editor', description: 'Collaborative coding environment', status: '‚úÖ' },
        { name: 'AI Text Recognition', description: 'Convert drawings to text', status: '‚úÖ' },
        { name: 'File Upload', description: 'Share images, PDFs, documents', status: '‚úÖ' },
        { name: 'Export Options', description: 'PNG, PDF, JSON, HTML exports', status: '‚úÖ' },
        { name: 'Activity Feed', description: 'Real-time activity tracking', status: '‚úÖ' }
      ]
    },
    {
      category: 'üìä Productivity',
      items: [
        { name: 'Pomodoro Timer', description: 'Focus sessions with breaks', status: '‚úÖ' },
        { name: 'Presentation Mode', description: 'Turn board into slides', status: '‚úÖ' },
        { name: 'Laser Pointer', description: 'Point and highlight content', status: '‚úÖ' },
        { name: 'Room Analytics', description: 'Usage stats & insights', status: '‚úÖ' },
        { name: 'Share & Export', description: 'Generate shareable links', status: '‚úÖ' }
      ]
    }
  ];

  const totalFeatures = features.reduce((sum, category) => sum + category.items.length, 0);
  const completedFeatures = features.reduce((sum, category) => 
    sum + category.items.filter(item => item.status === '‚úÖ').length, 0
  );

  const demoFeature = (featureName) => {
    const demos = {
      'Advanced Drawing Tools': 'Try the pen, eraser, rectangle, and circle tools!',
      'Sticky Notes': 'Click the yellow + button to add collaborative notes!',
      'Voice Chat': 'Record voice messages to communicate with your team!',
      'Live Polls': 'Create polls to make group decisions quickly!',
      'Code Editor': 'Write code together in real-time!',
      'AI Mind Maps': 'Generate structured mind maps from any topic!',
      'Laser Pointer': 'Activate laser pointer for presentations!',
      'Room Themes': 'Change the visual theme of your workspace!',
      'Emoji Reactions': 'React with emojis that float across the screen!'
    };

    showToast(demos[featureName] || `Try the ${featureName} feature!`, 'info');
  };

  return (
    <div className="p-4 feature-card m-2">
      <div className="flex items-center justify-between mb-3">
        <h3 className="text-sm font-bold text-gray-900 flex items-center">
          <span className="text-2xl mr-2">üöÄ</span>
          Feature Showcase
        </h3>
        <button
          onClick={() => setShowShowcase(!showShowcase)}
          className="px-4 py-2 bg-gradient-to-r from-purple-600 via-blue-600 to-indigo-600 text-white text-xs rounded-lg hover:from-purple-700 hover:via-blue-700 hover:to-indigo-700 transform hover:scale-105 transition-all duration-200 shadow-lg"
        >
          {showShowcase ? 'üîΩ Hide' : '‚ú® Show'} Features
        </button>
      </div>

      {/* Progress overview */}
      <div className="mb-4 p-4 bg-gradient-to-r from-green-50 via-blue-50 to-purple-50 rounded-xl border border-green-200 shadow-inner">
        <div className="flex items-center justify-between mb-3">
          <span className="text-sm font-bold text-gray-900 flex items-center">
            <span className="text-lg mr-2">üìä</span>
            Hackathon Progress
          </span>
          <span className="px-3 py-1 bg-gradient-to-r from-green-500 to-blue-500 text-white text-xs font-bold rounded-full shadow-lg">
            {completedFeatures}/{totalFeatures} Features ‚ú®
          </span>
        </div>
        <div className="w-full bg-gray-200 rounded-full h-3 shadow-inner">
          <div 
            className="bg-gradient-to-r from-green-400 via-blue-500 to-purple-500 h-3 rounded-full transition-all duration-1000 shadow-lg animate-pulse"
            style={{ width: `${(completedFeatures / totalFeatures) * 100}%` }}
          ></div>
        </div>
        <div className="text-xs text-gray-700 mt-2 font-medium text-center">
          üéâ All core features implemented! Ready for demo! üèÜ
        </div>
      </div>

      {showShowcase && (
        <div className="space-y-4 max-h-96 overflow-y-auto">
          {features.map((category, categoryIndex) => (
            <div key={categoryIndex} className="border border-gray-200 rounded-lg p-3">
              <h4 className="text-sm font-bold text-gray-800 mb-2">{category.category}</h4>
              <div className="space-y-2">
                {category.items.map((item, itemIndex) => (
                  <div key={itemIndex} className="flex items-center justify-between p-2 bg-gray-50 rounded">
                    <div className="flex-1">
                      <div className="flex items-center space-x-2">
                        <span className="text-lg">{item.status}</span>
                        <span className="text-sm font-medium">{item.name}</span>
                      </div>
                      <div className="text-xs text-gray-600 mt-1">{item.description}</div>
                    </div>
                    <button
                      onClick={() => demoFeature(item.name)}
                      className="px-2 py-1 bg-blue-100 text-blue-700 text-xs rounded hover:bg-blue-200"
                    >
                      Demo
                    </button>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Hackathon highlights */}
      <div className="mt-4 p-3 bg-yellow-50 rounded-md">
        <div className="text-sm font-bold text-yellow-800 mb-2">üèÜ Hackathon Highlights:</div>
        <div className="text-xs text-yellow-700 space-y-1">
          <div>‚Ä¢ <strong>20+ Features</strong> - Comprehensive collaboration platform</div>
          <div>‚Ä¢ <strong>Real-time Sync</strong> - Everything updates instantly</div>
          <div>‚Ä¢ <strong>AI-Powered</strong> - Smart text recognition & mind maps</div>
          <div>‚Ä¢ <strong>Multi-Modal</strong> - Drawing, coding, voice, video</div>
          <div>‚Ä¢ <strong>Export Ready</strong> - Multiple export formats</div>
          <div>‚Ä¢ <strong>Mobile Responsive</strong> - Works on all devices</div>
        </div>
      </div>

      {/* Tech stack */}
      <div className="mt-3 p-3 bg-blue-50 rounded-md">
        <div className="text-sm font-bold text-blue-800 mb-2">‚ö° Tech Stack:</div>
        <div className="flex flex-wrap gap-1">
          {['React', 'Node.js', 'Socket.io', 'MongoDB', 'Tailwind CSS', 'Canvas API', 'WebRTC'].map(tech => (
            <span key={tech} className="px-2 py-1 bg-blue-200 text-blue-800 text-xs rounded-full">
              {tech}
            </span>
          ))}
        </div>
      </div>

      {/* Call to action */}
      <div className="mt-3 text-center">
        <div className="text-xs text-gray-600 mb-2">
          üéØ Ready to impress the judges? This platform rivals professional tools like Miro & Figma!
        </div>
        <button
          onClick={() => showToast('üöÄ Your hackathon project is ready to win! All features are live and working!', 'success')}
