import React, { useState, useEffect } from 'react';
import Whiteboard from './Whiteboard';
import NotesEditor from './NotesEditor';
import UsersList from './UsersList';
import RoomHeader from './RoomHeader';
import VoiceChat from './VoiceChat';
import ScreenShare from './ScreenShare';
import AITextRecognition from './AITextRecognition';
import RealTimePolls from './RealTimePolls';
import PomodoroTimer from './PomodoroTimer';
import FileUpload from './FileUpload';
import ActivityFeed from './ActivityFeed';
import QuickTemplates from './QuickTemplates';
import PresentationMode from './PresentationMode';
import RoomAnalytics from './RoomAnalytics';
import CodeEditor from './CodeEditor';
import EmojiReactions from './EmojiReactions';
import LaserPointer from './LaserPointer';
import RoomThemes from './RoomThemes';
import ExportShare from './ExportShare';
import MindMapGenerator from './MindMapGenerator';
import FeatureShowcase from './FeatureShowcase';
import RealTimeChat from './RealTimeChat';
import LiveNotifications from './LiveNotifications';
import AIChatbot from './AIChatbot';
import BackendConnectionTest from './BackendConnectionTest';

const CollaborationRoom = ({ user, roomId, socket, onLeaveRoom, showToast }) => {
  const [activeTab, setActiveTab] = useState('whiteboard');
  const [users, setUsers] = useState([]);
  const [roomData, setRoomData] = useState({
    whiteboardData: [],
    notesData: ''
  });

  // Handle socket events
  useEffect(() => {
    if (!socket) return;

    // Handle room data loading
    const handleRoomData = (data) => {
      setRoomData({
        whiteboardData: data.whiteboardData || [],
        notesData: data.notesData || ''
      });
    };

    // Handle users list updates
    const handleUsersUpdate = (usersList) => {
      setUsers(usersList);
    };

    // Handle user join notifications
    const handleUserJoined = (data) => {
      if (data.username !== user.username) {
        showToast(`${data.username} joined the room`, 'info');
      }
    };

    // Handle user leave notifications
    const handleUserLeft = (data) => {
      showToast(`${data.username} left the room`, 'info');
    };

    // Handle connection errors
    const handleError = (error) => {
      showToast(error.message || 'An error occurred', 'error');
    };

    // Register event listeners
    socket.on('room-data', handleRoomData);
    socket.on('users-update', handleUsersUpdate);
    socket.on('user-joined', handleUserJoined);
    socket.on('user-left', handleUserLeft);
    socket.on('error', handleError);

    return () => {
      socket.off('room-data', handleRoomData);
      socket.off('users-update', handleUsersUpdate);
      socket.off('user-joined', handleUserJoined);
      socket.off('user-left', handleUserLeft);
      socket.off('error', handleError);
    };
  }, [socket, user.username, showToast]);

  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };

  return (
    <div className="min-h-screen relative overflow-hidden">
      {/* Animated Background Elements */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div className="absolute -top-40 -right-40 w-80 h-80 bg-gradient-to-br from-purple-400/20 to-pink-400/20 rounded-full blur-3xl animate-pulse"></div>
        <div className="absolute -bottom-40 -left-40 w-80 h-80 bg-gradient-to-tr from-blue-400/20 to-cyan-400/20 rounded-full blur-3xl animate-pulse" style={{animationDelay: '2s'}}></div>
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-96 h-96 bg-gradient-to-r from-indigo-400/10 to-purple-400/10 rounded-full blur-3xl animate-pulse" style={{animationDelay: '4s'}}></div>
      </div>

      {/* Room Header */}
      <div className="relative z-10">
        <RoomHeader
          user={user}
          roomId={roomId}
          onLeaveRoom={onLeaveRoom}
          showToast={showToast}
        />
      </div>

      <div className="flex flex-1 h-[calc(100vh-4rem)] relative z-10">
        {/* Main Content Area */}
        <div className="flex-1 flex flex-col">
          {/* Ultra-Modern Tab Navigation */}
          <div className="glass-effect border-b border-white/20 shadow-2xl relative">
            <div className="absolute inset-0 bg-gradient-to-r from-purple-500/10 via-blue-500/10 to-pink-500/10"></div>
            <div className="px-8 py-2 relative z-10">
              <nav className="flex space-x-2 overflow-x-auto py-4">
                <button
                  onClick={() => handleTabChange('whiteboard')}
                  className={`px-6 py-3 rounded-2xl font-bold text-sm transition-all duration-300 whitespace-nowrap relative overflow-hidden ${activeTab === 'whiteboard'
                    ? 'bg-gradient-to-r from-blue-500 to-purple-500 text-white shadow-2xl transform scale-105'
                    : 'bg-white/20 text-white/80 hover:bg-white/30 hover:text-white hover:scale-102 backdrop-blur-sm'
                    }`}
                >
                  <span className="relative z-10 flex items-center">
                    <span className="text-lg mr-2"></span>
                    Whiteboard
                  </span>
                  {activeTab === 'whiteboard' && (
                    <div className="absolute inset-0 bg-gradient-to-r from-blue-400 to-purple-400 opacity-20 animate-pulse"></div>
                  )}
                </button>
                <button
                  onClick={() => handleTabChange('notes')}
                  className={`py-3 px-4 border-b-3 font-semibold text-sm transition-all duration-200 whitespace-nowrap ${activeTab === 'notes'
                    ? 'border-green-500 text-green-600 bg-green-50/50'
                    : 'border-transparent text-gray-600 hover:text-green-600 hover:border-green-300 hover:bg-green-50/30'
                    }`}
                >
                   Notes
                </button>
                <button
                  onClick={() => handleTabChange('code')}
                  className={`py-3 px-4 border-b-3 font-semibold text-sm transition-all duration-200 whitespace-nowrap ${activeTab === 'code'
                    ? 'border-purple-500 text-purple-600 bg-purple-50/50'
                    : 'border-transparent text-gray-600 hover:text-purple-600 hover:border-purple-300 hover:bg-purple-50/30'
                    }`}
                >
                   Code Editor
                </button>
                <button
                  onClick={() => handleTabChange('mindmap')}
                  className={`py-3 px-4 border-b-3 font-semibold text-sm transition-all duration-200 whitespace-nowrap ${activeTab === 'mindmap'
                    ? 'border-pink-500 text-pink-600 bg-pink-50/50'
                    : 'border-transparent text-gray-600 hover:text-pink-600 hover:border-pink-300 hover:bg-pink-50/30'
                    }`}
                >
                   Mind Maps
                </button>
                <button
                  onClick={() => handleTabChange('polls')}
                  className={`py-3 px-4 border-b-3 font-semibold text-sm transition-all duration-200 whitespace-nowrap ${activeTab === 'polls'
                    ? 'border-teal-500 text-teal-600 bg-teal-50/50'
                    : 'border-transparent text-gray-600 hover:text-teal-600 hover:border-teal-300 hover:bg-teal-50/30'
                    }`}
                >
                   Live Polls
                </button>
                <button
                  onClick={() => handleTabChange('templates')}
                  className={`py-3 px-4 border-b-3 font-semibold text-sm transition-all duration-200 whitespace-nowrap ${activeTab === 'templates'
                    ? 'border-indigo-500 text-indigo-600 bg-indigo-50/50'
                    : 'border-transparent text-gray-600 hover:text-indigo-600 hover:border-indigo-300 hover:bg-indigo-50/30'
                    }`}
                >
                   Templates
                </button>
                <button
                  onClick={() => handleTabChange('analytics')}
                  className={`py-3 px-4 border-b-3 font-semibold text-sm transition-all duration-200 whitespace-nowrap ${activeTab === 'analytics'
                    ? 'border-orange-500 text-orange-600 bg-orange-50/50'
                    : 'border-transparent text-gray-600 hover:text-orange-600 hover:border-orange-300 hover:bg-orange-50/30'
                    }`}
                >
                   Analytics
                </button>
                <button
                  onClick={() => handleTabChange('files')}
                  className={`py-3 px-4 border-b-3 font-semibold text-sm transition-all duration-200 whitespace-nowrap ${activeTab === 'files'
                    ? 'border-cyan-500 text-cyan-600 bg-cyan-50/50'
                    : 'border-transparent text-gray-600 hover:text-cyan-600 hover:border-cyan-300 hover:bg-cyan-50/30'
                    }`}
                >
                   Files
                </button>
                <button
                  onClick={() => handleTabChange('ai')}
                  className={`py-3 px-4 border-b-3 font-semibold text-sm transition-all duration-200 whitespace-nowrap ${activeTab === 'ai'
                    ? 'border-purple-500 text-purple-600 bg-purple-50/50'
                    : 'border-transparent text-gray-600 hover:text-purple-600 hover:border-purple-300 hover:bg-purple-50/30'
                    }`}
                >
                   AI Assistant
                </button>
              </nav>
            </div>
          </div>

          {/* Tab Content */}
          <div className="flex-1 overflow-hidden">
            {activeTab === 'whiteboard' && (
              <Whiteboard
                socket={socket}
                roomId={roomId}
                initialData={roomData.whiteboardData}
                showToast={showToast}
              />
            )}
            {activeTab === 'notes' && (
              <NotesEditor
                socket={socket}
                roomId={roomId}
                initialContent={roomData.notesData}
                showToast={showToast}
              />
            )}
            {activeTab === 'code' && (
              <div className="h-full p-6 bg-gray-900">
                <CodeEditor
                  socket={socket}
                  roomId={roomId}
                  showToast={showToast}
                />
              </div>
            )}
            {activeTab === 'mindmap' && (
              <div className="h-full p-6 bg-gradient-to-br from-purple-50 to-pink-50">
                <div className="max-w-4xl mx-auto">
                  <MindMapGenerator
                    socket={socket}
                    roomId={roomId}
                    showToast={showToast}
                  />
                  <div className="mt-6 p-4 bg-white rounded-lg shadow-sm">
                    <h3 className="text-lg font-bold text-gray-900 mb-4"> AI Mind Map Generator</h3>
                    <p className="text-gray-600 mb-4">
                      Create structured mind maps from any topic using AI. Perfect for brainstorming,
                      project planning, and organizing ideas collaboratively.
                    </p>
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                      <div className="p-3 bg-purple-50 rounded-lg">
                        <h4 className="font-semibold text-purple-800"> Enter Topic</h4>
                        <p className="text-sm text-purple-600">Type any subject or idea</p>
                      </div>
                      <div className="p-3 bg-blue-50 rounded-lg">
                        <h4 className="font-semibold text-blue-800"> AI Generation</h4>
                        <p className="text-sm text-blue-600">Smart structure creation</p>
                      </div>
                      <div className="p-3 bg-green-50 rounded-lg">
                        <h4 className="font-semibold text-green-800"> Apply to Board</h4>
                        <p className="text-sm text-green-600">Visual mind map on canvas</p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            )}
            {activeTab === 'polls' && (
              <div className="h-full p-6 bg-gradient-to-br from-teal-50 to-blue-50">
                <div className="max-w-4xl mx-auto">
                  <div className="mb-6">
                    <h2 className="text-2xl font-bold text-gray-900 mb-2"> Live Polling System</h2>
                    <p className="text-gray-600">Create interactive polls and make group decisions in real-time</p>
                  </div>
                  <RealTimePolls
                    socket={socket}
                    roomId={roomId}
                    currentUser={user}
                    showToast={showToast}
                  />
                  <div className="mt-6 grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div className="p-4 bg-white rounded-lg shadow-sm">
                      <h3 className="font-bold text-gray-900 mb-2"> Features</h3>
                      <ul className="text-sm text-gray-600 space-y-1">
                        <li> Real-time voting with live results</li>
                        <li> Multiple choice questions</li>
                        <li> Visual progress bars</li>
                        <li> Instant notifications</li>
                      </ul>
                    </div>
                    <div className="p-4 bg-white rounded-lg shadow-sm">
                      <h3 className="font-bold text-gray-900 mb-2"> Use Cases</h3>
                      <ul className="text-sm text-gray-600 space-y-1">
                        <li> Team decision making</li>
                        <li> Meeting feedback</li>
                        <li> Quick surveys</li>
                        <li> Brainstorming votes</li>
                      </ul>
                    </div>
                  </div>
                </div>
              </div>
            )}
            {activeTab === 'templates' && (
              <div className="h-full p-6 bg-gradient-to-br from-indigo-50 to-purple-50">
                <div className="max-w-4xl mx-auto">
                  <div className="mb-6">
                    <h2 className="text-2xl font-bold text-gray-900 mb-2"> Quick Templates</h2>
                    <p className="text-gray-600">Professional templates for meetings, planning, and collaboration</p>
                  </div>
                  <QuickTemplates
                    socket={socket}
                    roomId={roomId}
                    showToast={showToast}
                  />
                  <div className="mt-6 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    <div className="p-4 bg-white rounded-lg shadow-sm">
                      <h3 className="font-bold text-gray-900 mb-2"> Meeting Agenda</h3>
                      <p className="text-sm text-gray-600">Structured meeting format with time slots</p>
                    </div>
                    <div className="p-4 bg-white rounded-lg shadow-sm">
                      <h3 className="font-bold text-gray-900 mb-2"> Brainstorming</h3>
                      <p className="text-sm text-gray-600">Creative ideation framework</p>
                    </div>
                    <div className="p-4 bg-white rounded-lg shadow-sm">
                      <h3 className="font-bold text-gray-900 mb-2"> SWOT Analysis</h3>
                      <p className="text-sm text-gray-600">Strategic planning matrix</p>
                    </div>
                  </div>
                </div>
              </div>
            )}
            {activeTab === 'analytics' && (
              <div className="h-full p-6 bg-gradient-to-br from-orange-50 to-red-50">
                <div className="max-w-4xl mx-auto">
                  <div className="mb-6">
                    <h2 className="text-2xl font-bold text-gray-900 mb-2"> Room Analytics</h2>
                    <p className="text-gray-600">Real-time insights and collaboration metrics</p>
                  </div>
                  <RoomAnalytics
                    socket={socket}
                    roomId={roomId}
                    users={users}
                  />
                  <div className="mt-6 grid grid-cols-1 md:grid-cols-3 gap-6">
                    <div className="p-4 bg-white rounded-lg shadow-sm text-center">
                      <div className="text-3xl font-bold text-blue-600">{users.length}</div>
                      <div className="text-sm text-gray-600">Active Users</div>
                    </div>
                    <div className="p-4 bg-white rounded-lg shadow-sm text-center">
                      <div className="text-3xl font-bold text-green-600">{Math.floor(Math.random() * 50 + 10)}</div>
                      <div className="text-sm text-gray-600">Actions Today</div>
                    </div>
                    <div className="p-4 bg-white rounded-lg shadow-sm text-center">
                      <div className="text-3xl font-bold text-purple-600">{Math.floor(Math.random() * 60 + 15)}m</div>
                      <div className="text-sm text-gray-600">Session Time</div>
                    </div>
                  </div>
                </div>
              </div>
            )}
            {activeTab === 'files' && (
              <div className="h-full p-6 bg-gradient-to-br from-cyan-50 to-blue-50">
                <div className="max-w-4xl mx-auto">
                  <div className="mb-6">
                    <h2 className="text-2xl font-bold text-gray-900 mb-2"> File Sharing</h2>
                    <p className="text-gray-600">Upload and share files with your team in real-time</p>
                  </div>
                  <FileUpload
                    socket={socket}
                    roomId={roomId}
                    showToast={showToast}
                  />
                  <div className="mt-6">
                    <ExportShare
                      socket={socket}
                      roomId={roomId}
                      showToast={showToast}
                    />
                  </div>
                  <div className="mt-6 grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div className="p-4 bg-white rounded-lg shadow-sm">
                      <h3 className="font-bold text-gray-900 mb-2"> Upload</h3>
                      <ul className="text-sm text-gray-600 space-y-1">
                        <li> Images (PNG, JPG, GIF)</li>
                        <li> Documents (PDF, TXT)</li>
                        <li> Drag & drop support</li>
                        <li> 5MB file size limit</li>
                      </ul>
                    </div>
                    <div className="p-4 bg-white rounded-lg shadow-sm">
                      <h3 className="font-bold text-gray-900 mb-2"> Export</h3>
                      <ul className="text-sm text-gray-600 space-y-1">
                        <li> PNG image exports</li>
                        <li> PDF documents</li>
                        <li> JSON data format</li>
                        <li> HTML web pages</li>
                      </ul>
                    </div>
                  </div>
                </div>
              </div>
            )}
            {activeTab === 'ai' && (
              <div className="h-full p-6 bg-gradient-to-br from-purple-50 to-indigo-50">
                <div className="max-w-4xl mx-auto">
                  <div className="mb-6">
                    <h2 className="text-2xl font-bold text-gray-900 mb-2"> AI Assistant</h2>
                    <p className="text-gray-600">Powered by Google Gemini - Your intelligent collaboration partner</p>
                  </div>
                  <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
                    <div className="lg:col-span-2">
                      <AIChatbot
                        socket={socket}
                        roomId={roomId}
                        showToast={showToast}
                      />
                    </div>
                    <div className="space-y-4">
                      <div className="p-4 bg-white rounded-lg shadow-sm">
                        <h3 className="font-bold text-gray-900 mb-2"> AI Capabilities</h3>
                        <ul className="text-sm text-gray-600 space-y-1">
                          <li> Brainstorming assistance</li>
                          <li> Code review & suggestions</li>
                          <li> Project planning help</li>
                          <li> Meeting facilitation</li>
                          <li> Content generation</li>
                          <li> Problem solving</li>
                        </ul>
                      </div>
                      <div className="p-4 bg-white rounded-lg shadow-sm">
                        <h3 className="font-bold text-gray-900 mb-2"> Quick Actions</h3>
                        <div className="space-y-2">
                          <button
                            onClick={() => showToast('Ask AI: "Help me plan our sprint"', 'info')}
                            className="w-full text-left p-2 text-sm bg-purple-50 hover:bg-purple-100 rounded transition-colors"
                          >
                             Sprint Planning
                          </button>
                          <button
                            onClick={() => showToast('Ask AI: "Review this code structure"', 'info')}
                            className="w-full text-left p-2 text-sm bg-blue-50 hover:bg-blue-100 rounded transition-colors"
                          >
                             Code Review
                          </button>
                          <button
                            onClick={() => showToast('Ask AI: "Generate meeting agenda"', 'info')}
